import React, { useEffect } from 'react';
import { checkServiceConnection } from './functions/functions';

const baseURL = "https://www.strava.com/api/v3";

export default function GetRuns() {

    const [postdata, setPostData] = React.useState([]);
    const [isLoading, setLoading] = React.useState(true); 
    const apiFunction = "/athlete/activities";

    const fetchData = () => {
        if (checkServiceConnection("strava")) {

            fetch(baseURL + apiFunction, {
                headers: {
                    Authorization: "Bearer " + localStorage.getItem("strava_access_token")
                }
            })
            .then(res => res.json())
            .then((response) => {

                setPostData(response);

                setLoading(false);
            })
            .catch((err) => console.log("error"));
        }
    }

    useEffect(() => {
        fetchData();
    }, []);

    if (isLoading) {
        return <div><p style={{color: '#000'}}>Loading...</p></div>
    }

    postdata.map((activity) => {
        console.log(activity.distance);
        return (
            <p key={ activity.id }>{ activity.distance } meters</p>
        );
    });
}
