import axios from 'axios';
import React, { useEffect } from 'react';

export default function Handle(props) {

    const urlParameters = new URLSearchParams(window.location.search);

    const service_name = props.service_name;
    let authcode = null;

    const [responseData, setResponseData] = React.useState({});
    const [isLoading, setLoading] = React.useState(true); 

    switch (service_name) {
        case "strava":
            authcode = urlParameters.get("code");
            break;

        case "fitbit":
            authcode = urlParameters.get("code");
            break;

        case "lastfm":
            authcode = urlParameters.get("token");
            break;

        case "deezer": 
            authcode = urlParameters.get("code");
            break;
    }

    const options = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ 
            code_verify: localStorage.getItem("fitbitCV"),
            service_name: service_name,
            auth_code: authcode
        })
    }

    localStorage.removeItem(service_name + '_access_token');
    localStorage.removeItem(service_name + '_refresh_token');
    localStorage.removeItem(service_name + '_expiry_date');

    useEffect(() => {
        fetch("/get-session", options)
            .then((res) => res.json())
            .then((data) => {

                setResponseData(data);
                if (data.hasOwnProperty('token')) {
                    localStorage.setItem(service_name + '_access_token', responseData.token);
                }
                if (data.hasOwnProperty('exp_date')) {
                    localStorage.setItem(service_name + '_expiry_date', responseData.exp_date);
                }
                if (data.hasOwnProperty('refresh_token')) {
                    localStorage.setItem(service_name + '_refresh_token', responseData.refresh_token);
                }
                setLoading(false);
            });

    }, []);

    if (isLoading) {
        return <div>Loading...</div>
    }

    return (
        <div>
            <p style={{color: '#000'}}>{ "Access Token: " + localStorage.getItem(service_name + '_access_token') }</p>
            <p style={{color: '#000'}}>{ "Expiry Date: " + localStorage.getItem(service_name + '_expiry_date') }</p>
            <p style={{color: '#000'}}>{ "Refresh Token: " + localStorage.getItem(service_name + '_refresh_token') }</p>
        </div>
    );

}
