import React, {useState, useEffect} from 'react';
import { checkServiceConnection, checkSpotifyConnection, spotifySearch, audioFeatures } from '../functions/functions';

export default function Test() {

    const [response, setResponse] = useState([]);
    const [isLoading, setLoading] = useState(true)
    let trackInfo = null;


    const fetchData = () => {
        if (checkServiceConnection("lastfm")) {
            fetch("/get-lastfm-songs?start_date=1672409068&end_date=1672436308&user=" + localStorage.getItem("lastfm_name"))
                .then(res => res.json())
                .then((response) => {
                    console.log(response)
                    setResponse(response.response.recenttracks.track);
                });
        }

        let seedList = "";
        let trackID = null;

        for (let song of response) {

            // song.name = track
            // song.artist.name = artist
            // song.album.#text = album
            console.log(song)

            spotifySearch(song.artist.name, null, song.album['#text'], song.name, function(response) {
                trackID = response.tracks.items[0].id;

                if (trackID !== null) {
                    if (seedList !== "") {
                        seedList += ",";
                    }
                    seedList += trackID;
                }

            });

        }

        //multiple track analysis 
        trackInfo = audioFeatures(seedList);

        console.log(trackInfo);

        setLoading(false);
    }

    useEffect(() => {
        fetchData();
    }, []);

    if (isLoading) {
        return "Loading";
    }





    return (
        <div>
            <p>Loaded</p>
        </div>
    )
}
