import axios from 'axios';
import React from 'react';

const md5 = require('md5')

export default function LastfmHandle() {

    const baseURL = "http://ws.audioscrobbler.com/2.0";
    const urlParameters = new URLSearchParams(window.location.search);
    const authcode = urlParameters.get("token");
    const {REACT_APP_LASTFM_API_KEY, REACT_APP_LASTFM_CLIENT_SECRET} = process.env;

    const [postdata, setPostData] = React.useState(null);

    const api_sig = md5("api_key" + REACT_APP_LASTFM_API_KEY + "methodauth.getSessiontoken" + authcode + REACT_APP_LASTFM_CLIENT_SECRET)

    function authenticateApplication() {
        axios
            .post(baseURL, {
                method: "auth.getSession",
                api_key: REACT_APP_LASTFM_API_KEY,
                token: authcode,
                api_sig: api_sig
            })
            .then((response) => {
                setPostData(response.data);
                console.log(response.data);
            });

    }

    authenticateApplication()

    return (
        <div>
            <p style={{color: '#000'}}> {  }</p>
        </div>
    );
}
