import axios from 'axios';
import React from 'react';

export default function StravaHandle() {

    const baseURL = "https://www.strava.com/oauth/token";
    const urlParameters = new URLSearchParams(window.location.search);
    const authcode = urlParameters.get("code");
    const {REACT_APP_STRAVA_CLIENT_ID, REACT_APP_STRAVA_CLIENT_SECRET} = process.env;

    const [postdata, setPostData] = React.useState(null);

    //function authenticateApplication() {
        axios
            .post(baseURL, {
                client_id: REACT_APP_STRAVA_CLIENT_ID,
                client_secret: REACT_APP_STRAVA_CLIENT_SECRET,
                code: authcode,
                grant_type: "authorize"
            })
            .then((response) => {
                setPostData(response.data);
            });

    //}

    return (
        <p style={{color: '#000'}}> { postdata }</p>
    );
}
