import React, {useState} from 'react';

export default function Parameters({ addParams }) {

    const [tempo, setTempo] = useState();
    const [loudness, setLoudness] = useState();
    const [key, setKey] = useState();
    const [acousticness, setAcousticness] = useState();
    const [speechiness, setSpeechiness] = useState();

    const handleSubmit = (e) => {
        addParams([tempo,loudness,key,acousticness,speechiness]);
        e.preventDefault();
    }

    return (
        <form onSubmit={e => { handleSubmit(e) }}>
            <input name="tempo" type="number" step="1" min="1" value={ tempo } onChange={e => setTempo(e.target.value)} />
            <input name="loudness" type="number" step="1" min="-60" value={ loudness } onChange={e => setLoudness(e.target.value)}/>
            <input name="" type="number" step="" value={key} onchange={e => setKey(e.target.value)}/>
            <input />
            <input />
        </form>
    )
}
