import React from 'react';

const ProgressBar = (props) => {
    const { percentage } = props;

    const barContainerStyle = {
        height: '18px',
        width: '100%',
        backgroundColor: "#ffffff",
        opacity: '50%',
        borderRadius: 50
    };

    const barStyle = {
        height: '100%',
        width: percentage + '%',
        backgroundColor: '#fff',
        borderRadius: 50,
        opacity: 100
    };

    return (
        <div style={ barContainerStyle }>
            <div style={ barStyle }></div>
        </div>
    )
}

export default ProgressBar;
