import React from 'react';
import { Link } from 'react-router-dom'

const redirectURL = "http://localhost:3000/redirect";
const { REACT_APP_STRAVA_CLIENT_ID } = process.env;

console.log(process.env.REACT_APP_STRAVA_CLIENT_ID)

export default function Service(props) {
    return (
        <div className="service">
            <div className={"service-img " + props.api}></div>
            <div className={"service-type " + props.type}></div>
            <a className="service-connect" href={connectURL(props.api)}>Connect</a>
        </div>
    );
}

function connectURL(props) {
    let url = "";
    let response_type = "code";
    let scope = "";
    let approval_prompt = "force";

    switch(props) {
        case "strava":
            redirectURL = redirectURL + "#strava";
            scope = "read,activity:read";
            url = "https://www.strava.com/oauth/authorize?";
            url = url + "client_id=" + REACT_APP_STRAVA_CLIENT_ID + "&response_type=" + response_type + "&redirect_uri=" + redirectURL + "&approval_prompt" + "&scope=" + scope;
            break;
        case "fitbit": 
            scope = ""
            url = "https://www.fitbit.com/oauth2/authorize";
            break;
        case "lastfm":

            url = "http://www.last.fm/api/auth";
            break;
        case "deezer": 

            url = "https://connect.deezer.com/oauth/auth.php";
            break;
    }

    return url;
}
